import java.util.Scanner;

public class b10869 {

	public static void main(String[] args) throws IOException {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int first, second;
		first = sc.nextInt();
		second = sc.nextInt();
		System.out.println(first+second);
		System.out.println(first-second);
		System.out.println(first*second);
		System.out.println(first/second);
		System.out.println(first%second);
	}

}
